using namespace std;

int numfactor(int n){
  int ret=1, temp=0;
  for(int i=2; i<=n; i++){
    temp=0;
    while(n%i==0){
      temp++;
      n=n/i;
    }
    ret*=(temp+1);
  }
  return ret;}

int main()
{
  for(int i=1; i<10000; i++){
    if(numfactor(i)==4 && numfactor(i+1)==4 && numfactor(i+2)==4){
      cout << i << endl;
      break;
    }
  }
}

